// 简化版Axios.js - 用于支持基本的HTTP请求功能
const axios = {
    get: function(url) {
        return new Promise((resolve, reject) => {
            console.log('模拟GET请求:', url);
            // 模拟延迟响应
            setTimeout(() => {
                resolve({
                    data: {
                        message: '模拟响应',
                        success: true
                    },
                    status: 200
                });
            }, 100);
        });
    },
    post: function(url, data) {
        return new Promise((resolve, reject) => {
            console.log('模拟POST请求:', url, data);
            // 模拟延迟响应
            setTimeout(() => {
                resolve({
                    data: {
                        message: '模拟响应',
                        success: true,
                        data: data
                    },
                    status: 200
                });
            }, 100);
        });
    },
    put: function(url, data) {
        return this.post(url, data);
    },
    delete: function(url) {
        return this.get(url);
    }
};

window.axios = axios;